import React, {useState, useEffect} from 'react';
import {Link, useNavigate} from "react-router-dom";
import {FiUserPlus, FiLogOut, FiEdit, FiTrash2} from 'react-icons/fi';

import api from '../../services/api';

import './styles.css';

import logoJP from '../../assets/logoJP.png';
import {toast} from "react-toast";

export default function Clients() {

    const username = localStorage.getItem('username');
    const accessToken = localStorage.getItem('accessToken');

    const navigate = useNavigate();
    const [showConfirmation, setShowConfirmation] = useState(false);

    const [clients, setClients] = useState([]);
    const [page, setPage] = useState(0);

    const handleDeleteClick = () => {
        setShowConfirmation(true);
    };

    async function handleConfirmDelete (id){
        deleteClient(id);
        setShowConfirmation(false);
    };

    const handleCancelDelete = () => {
        setShowConfirmation(false);
    };

    async function logout() {
        localStorage.clear();
        navigate('/');
    }

    async function editClient(id) {
        try {
            navigate(`/client/new/${id}`);
        } catch (error) {
            toast.error('Edit Failed! Try Again.')
        }
    }


    async function deleteClient(id) {
        try {

            await api.delete(`/api/clients/${id}`, {
                headers: {
                    Authorization: `Bearer ${accessToken}`
                }
            })
            setClients(clients.filter(client => client.id !== id))
            toast.success('Client Deleted Sucessful!');
        } catch (err) {
            toast.error('Delete Failed!');
        }
    }

    async function fetchMoreClients() {
        const response = await api.get('/api/clients', {
            headers: {
                Authorization: `Bearer ${accessToken}`
            },
            params: {
                page: page,
                size: 4,
            }
        })

        setClients([...clients, ...response.data._embedded.clientDTOList])
        setPage(page + 1);
    }


// useEffect Ã© pra carregar a tela assim que carregar o HTML os dados virao!
    useEffect(() => {
        fetchMoreClients();
    }, [])

    return (
            <div className="client-container">
                <header>
                    <img src={logoJP} alt="JP"/>
                    <span>Welcome, <strong>{username.toUpperCase()}</strong></span>
                    <Link className="buttonClient" to="/client/new/0">
                        <div className="container-button">
                            <div className="iconUserPlus"><FiUserPlus size={24} color="white"/></div>
                            <div className="textButton">Add new Client</div>
                        </div>
                    </Link>

                    <button onClick={logout} className="buttonPower" type="button">
                        <FiLogOut size={18} color="#251FC5"/>
                    </button>

                </header>
                <h1>Registered Clients</h1>
                <ul>
                    {clients.map(client => (
                        <li key={client.id}>
                            <strong>Name:</strong>
                            <p>{client.name}</p>
                            <strong>E-mail:</strong>
                            <p>{client.email}</p>
                            <strong>RG:</strong>
                            <p>{client.rg}</p>
                            <strong>CPF:</strong>
                            <p>{client.cpf}</p>
                            <strong>Birthday:</strong>
                            <p>{Intl.DateTimeFormat('pt-BR').format(new Date(client.birthDay))}</p>
                            <strong>Cellphone:</strong>
                            <p>{client.cellphone}</p>
                            <strong>Adress:</strong>
                            {client.adress ? (
                                <p>{client.adress.street + ", " + client.adress.district + ", " + client.adress.number}</p>
                            ) : (
                                <p>EndereÃ§o nÃ£o disponÃ­vel</p>
                            )}

                            <button type="button">
                                <FiEdit onClick={() => editClient(client.id)} size={20} color="#251FC5"/>
                            </button>
                            <button onClick={() => handleDeleteClick()} type="button">
                                <FiTrash2 size={20} color="#251FC5"/>
                            </button>
                            {showConfirmation && (
                                <div className="modal">
                                    <div className="modal-content">
                                        <h2>ConfirmaÃ§Ã£o</h2>
                                        <p>Deseja realmente excluir este item?</p>
                                        <button onClick={handleConfirmDelete(client.id)}>Confirmar</button>
                                        <button onClick={handleCancelDelete}>Cancelar</button>
                                    </div>
                                </div>
                            )}
                        </li>
                    ))}
                </ul>
                <button className="buttonMorePage" onClick={fetchMoreClients} type="button"> Load More</button>
            </div>


    )
}
